"use strict";

var forestServerRequester = require('./forest-server-requester');

var logger = require('./logger');

var errorMessages = require('../utils/error-messages');

function AuthorizationFinder(renderingId, environmentSecret, twoFactorRegistration, email, password, forestToken) {
  this.perform = function () {
    var pathEnd;
    var headers;

    if (email && password) {
      pathEnd = 'authorization';
      headers = {
        email: email,
        password: password
      };
    } else if (forestToken) {
      pathEnd = 'google-authorization';
      headers = {
        'forest-token': forestToken
      };
    }

    var url = "/liana/v2/renderings/".concat(renderingId, "/").concat(pathEnd);

    if (twoFactorRegistration) {
      url += "?two-factor-registration=".concat(twoFactorRegistration);
    }

    return forestServerRequester.perform(url, environmentSecret, null, headers).then(function (result) {
      var user = result.data.attributes;
      user.id = result.data.id;
      return user;
    })["catch"](function (error) {
      logger.error('Authorization error: ', error);
      var errorMessageToForward;

      switch (error.message) {
        case errorMessages.SERVER_TRANSACTION.SECRET_AND_RENDERINGID_INCONSISTENT:
          errorMessageToForward = error.message;
          break;

        case errorMessages.SERVER_TRANSACTION.SECRET_NOT_FOUND:
          errorMessageToForward = 'Cannot retrieve the project you\'re trying to unlock. ' + 'Please check that you\'re using the right environment secret regarding your project and environment.';
          break;

        default:
          errorMessageToForward = undefined;
      }

      throw new Error(errorMessageToForward);
    });
  };
}

module.exports = AuthorizationFinder;